import ReactApexChart from 'react-apexcharts'
export default class DonutChart extends React.Component {
      
  constructor(props) {
    super(props);

    this.state = {
      options: {
        responsive: [{
          breakpoint: 480,
          options: {
            chart: {
              width: 200
            },
            legend: {
              // position: 'bottom'
            }
          }
        }]
      },
      series: [-20]
    }
  }

  render() {
    return (
      

      <div id="chart">
        <ReactApexChart options={this.state.options} series={this.state.series} type="donut" width="280" />
      </div>


    );
  }
}
